["2407"] = {["id"] = 2407,["name"] = "杨玉儿",["look"] = 55,["start"] = 4,["desc"] = "自幼聪慧，琴棋书画，无一不精，又能歌善舞，生就一副俊美人材，如此人儿嫁给秦琼，可谓是男才女貌。"},
["2408"] = {["id"] = 2408,["name"] = "杨义臣",["look"] = 20,["start"] = 4,["desc"] = "杨林的弟弟，曾摆下一个八面金锁阵，后被李元霸、裴元庆、秦用、梁士泰四猛八大锤所破，他自己自杀身亡。"},
["2409"] = {["id"] = 2409,["name"] = "杨素",["look"] = 27,["start"] = 4,["desc"] = "九位兴隋老臣之一，为人不拘小节，胸有大志，为大隋朝的建立立下赫赫战功。"},
["2410"] = {["id"] = 2410,["name"] = "梁师泰",["look"] = 49,["start"] = 4,["desc"] = "隋唐第十五名好汉，挂锤庄庄主。"},
