["2412"] = {["id"] = 2412,["name"] = "翠珠",["look"] = 55,["start"] = 4,["desc"] = "原本一丫鬟，加入瓦岗寨后竟习得一身武艺。"},
["2413"] = {["id"] = 2413,["name"] = "韩擒虎",["look"] = 28,["start"] = 4,["desc"] = "韩擒虎本名擒豹，据说他在十三岁时生擒过一头猛虎，于是改名为擒虎。"},
["2501"] = {["id"] = 2501,["name"] = "魏徵",["look"] = 50,["start"] = 5,["desc"] = "在四十六兄弟里排行老大，为人正直，博学多才，可谓是上知天文，下知地理的神级军师级人物。"},
["2502"] = {["id"] = 2502,["name"] = "魏文通",["look"] = 8,["start"] = 5,["desc"] = "隋唐第九好汉，隋朝潼关元帅，人送外号“花刀帅”“九省花刀将”，另外相貌与三国关羽相似，又号称赛关爷。"},
["2503"] = {["id"] = 2503,["name"] = "单雄信",["look"] = 15,["start"] = 5,["desc"] = "武艺出众，仗义疏财，深孚众望，性格倔强，不屈不挠，慷慨赴难。"},
["2504"] = {["id"] = 2504,["name"] = "秦琼",["look"] = 12,["start"] = 5,["desc"] = "是一个于万马军中取人首级如探囊取物的传奇式人物。"},
["2505"] = {["id"] = 2505,["name"] = "翟让",["look"] = 24,["start"] = 5,["desc"] = "隋末农民起义中瓦岗军前期领袖。"},
["2506"] = {["id"] = 2506,["name"] = "徐茂公",["look"] = 33,["start"] = 5,["desc"] = "唐初名将，与李靖并称，被封为英国公，为凌烟阁二十四功臣之一。"},
["2507"] = {["id"] = 2507,["name"] = "杨林",["look"] = 10,["start"] = 5,["desc"] = "隋炀帝的叔叔，隋唐第八好汉，总管隋朝兵权，为人正直无私，虽然很赏识秦琼等英雄，但对隋朝忠心耿耿，一心想挽回天下大势。"},
["2508"] = {["id"] = 2508,["name"] = "罗成",["look"] = 34,["start"] = 5,["desc"] = "胯下闪电白龙驹、手中五钩神飞枪。"},
["2509"] = {["id"] = 2509,["name"] = "程咬金",["look"] = 32,["start"] = 5,["desc"] = "外号“混世魔王”，人如其名，干的混事数不胜数，曾为瓦岗寨大魔国国王，后投降李唐，凭小聪明和运气也立了不少大功。"},
["2510"] = {["id"] = 2510,["name"] = "伍天锡",["look"] = 9,["start"] = 5,["desc"] = "两臂万斤之力，武艺高强，使一对混元流金鎲，最后死于天下第一好汉李元霸之手。"},
["2511"] = {["id"] = 2511,["name"] = "伍云召",["look"] = 29,["start"] = 5,["desc"] = "因人长着面如紫玉的俊美外表，拥有一身冠绝天下的武艺，加上被悲悯的身世，就好比三国时期的锦马超在世。"},
["2512"] = {["id"] = 2512,["name"] = "雄阔海",["look"] = 5,["start"] = 5,["desc"] = "绰号“紫面天王”，为隋唐第四好汉，善使长棍，英勇无比，在扬州战役中，因力托千斤闸、体力不支被压死。"},
["2513"] = {["id"] = 2513,["name"] = "裴元庆",["look"] = 3,["start"] = 5,["desc"] = "手使两把大锤，勇猛异常，四明山一战，十八路反王无数兵将，只有他能接李元霸三锤。"},
["2514"] = {["id"] = 2514,["name"] = "宇文成都",["look"] = 2,["start"] = 5,["desc"] = "隋唐第二好汉，虽为奸臣宇文化及之子，但行事光明磊落，从不耍奸诈计量，算得上一条好汉。"},
